package com.meta.brain.file.recovery

import androidx.lifecycle.LifecycleOwner
import com.meta.brain.module.base.MetaBrainApp
import com.meta.brain.module.data.DataManager

class App : MetaBrainApp() {
    override fun onStart(owner: LifecycleOwner) {
        super.onStart(owner)
        DataManager.setStartActivity(IntroActivity::class.java)
    }
}

package com.meta.brain.file.recovery

import android.util.Log
import com.meta.brain.file.recovery.databinding.IntroActivityBinding
import com.meta.brain.module.ads.AdEvent
import com.meta.brain.module.ads.AdsController
import com.meta.brain.module.base.DataBindActivity
import androidx.core.content.edit

class IntroActivity : DataBindActivity<IntroActivityBinding>(R.layout.intro_activity) {
    private val prefs by lazy { getSharedPreferences("metrics", MODE_PRIVATE) }
    override fun initView(){
        binding.btnStart.setOnClickListener {
            AdsController.showInter(this,object : AdEvent(){
                override fun onComplete() {
                    startHome()
                }
            })
        }
    }

    fun startHome(){
        val count = bumpStartTouchCount()
        Log.d("IntroActivity", "Start button touched $count time(s) ")
//        startActivity(Intent(this, HomeActivity::class.java))
//        finish()
    }
    private fun bumpStartTouchCount(): Int {
        val next = prefs.getInt("start_touches", 0) + 1
        prefs.edit { putInt("start_touches", next) }
        return next
    }
}
